import React, { useState } from 'react';

function PageNavBar({ currPage, pageList, topics }) {
    const [pageIndex, setPageIndex] = useState(1);

    // define event handlers 
    const goBack = () => {
        setPageIndex(prevIndex => prevIndex - 1);
    };

    const goNext = () => {
        setPageIndex(prevIndex => prevIndex + 1);
    };

    // determine if on the first question or not 
    const onFirstPage = pageIndex === 1;
    const onLastPage = pageIndex === pageList.length;

    let backButton = <></>;
    let nextButton = <></>;
    if(!onFirstPage){
        backButton = <button onClick={goBack}>{topics[(pageIndex-1)]}</button>
    }
    if(!onLastPage){
        nextButton = <button onClick={goNext}>{topics[pageIndex+1]}</button>
    }

    return (
        <>
            {backButton}
            {nextButton}
        </>
    );
}
export default PageNavBar;
