import React from 'react';

function PageNavBar({ currPage, pageList }) {
    const [pageIndex, setPageIndex] = useState(1);

    // define event handlers 
    const goBack = () => {
        setPageIndex(prevIndex => prevIndex - 1);
    };

    const goNext = () => {
        setPageIndex(prevIndex => prevIndex + 1);
    };

    // determine if on the first question or not 
    const onFirstPage = questionIndex === 1;
    const onLastPage = questionIndex === pageList.length;

    let backButton = <></>;
    let nextButton = <></>;
    if(!onFirstPage){
        backButton = <button onClick={goBack}></button>
    }
    if(!onLastPage){
        nextButton = <button disabled={onLastPage} onClick={goNext}></button>
    }

    return (
        <>
            <button disabled={onFirstPage} onClick={goBack}></button>
            <button disabled={onLastPage} onClick={goNext}></button>
        </>
    );
}
export default PageNavBar;
