import React from 'react';
import { Route, Routes } from 'react-router-dom';
import CustomLink from './CustomLink';
import Page from './Page';
import './Unit.css';

function Unit({ unit }){
    const topics = [
        ['Types of Variables', 'Converting Categorical Data into Numeric Data'],
        ['K-Means', 'Principal Component Analysis (PCA)']
    ];
    let subUnitList;
    if(unit - 1 < topics.length){
        subUnitList = topics[unit - 1].map((title, index) => <li><CustomLink to={`/unit${unit}${index}`} className='subunit'>{title}</CustomLink></li>);
    }
    let subUnitRoutes;
    if (unit - 1 < topics.length) {
        subUnitRoutes = topics[unit - 1].map((title, index) => <Route path={`/unit${unit}${index}`} element={<Page unit={unit} index={index} />}></Route>);
    }
    return (
        <div className={`unit${unit}`}>
            <h2>Unit {unit}</h2>
            <ul>{subUnitList}</ul>
            <Routes>{subUnitRoutes}</Routes>
        </div>
    );
};
export default Unit;
