import React from 'react';
import './Unit.css';

function Unit({ unit, subUnitList }){
    const unitNames = ['Introduction to Data Science and Supervised Learning', 'Unsupervised and Other Types of Learning', 'State-of-the-art Architectures', 'Applications']
    const overview = ['Data science is a field that uses statistics, \
    mathematics, and computer science to extract knowledge from large \
    amounts of data. Since the amount of digital information has grown \
    exponentially in the past few years, developing algorithms to find \
    patterns in data has become essential for informed decision-making. \
    These algorithms learn from the training data and adjust based on \
    feedback. The simplest type of learning is supervised learning, \
    which is when a model uses a labeled dataset to predict outputs \
    given a set of inputs.',
    'Unit 1 covered supervised learning, which requires labeled datasets. \
    While using labeled datasets is the most reliable way to train a model, \
    the most significant limitation on using labeled datasets is that they are expensive to hand-label. There is ',
    'unit3 intro',
    'unit4 intro']
    return (
        <div className="unit">
            <h2>Unit {unit} - {unitNames[unit-1]}</h2>
            <p>{overview[unit - 1]}</p>
            <ul>{subUnitList}</ul>
        </div>
    );
};
export default Unit;
