import React, { useState, useEffect } from 'react';
import './Home.css'

function Home() {
  useEffect(() => {
    let text = ['algorithms are hard.', 'money.', 'you have leftover GPUs from mining bitcoin.'];
    var stringIndex = 0;
    var charIndex = 0;
    let speed = 50;
    let pause = 1000;
    typeWriter(text);
  });

  function typeWriter(text) {
    if (charIndex < text[stringIndex].length) {
      document.getElementById("typedtext").innerHTML += text[stringIndex].charAt(charIndex);
      charIndex++;
      setTimeout(() => {typeWriter(text)}, speed);
    }
    else {
      setTimeout(() => {typeDeleter(text)}, pause);
    }
  }
  function typeDeleter(text) {
    if (charIndex > 0) {
      document.getElementById("typedtext").innerHTML = document.getElementById("typedtext").innerHTML.slice(0, -1);
      charIndex--;
      setTimeout(() => {typeDeleter(text)}, speed);
    }
    else {
      stringIndex = (stringIndex+1) % text.length;
      setTimeout(() => {typeWriter(text)}, pause);
    }
  }

  return (
  <div className="home">
    <div className="tagline">
      <h1>Deep Learning</h1>
      <h5>Because algorithms are too hard</h5>
      <h5 id="typedtext"></h5>
    </div>
  </div>);
}
export default Home;
