import React, { useState, useEffect } from 'react';
import './Home.css'

function Home() {
  const [stringIndex, setStringIndex] = useState(0);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    let text = [
      'algorithms are too hard.',
      'Attention Is All You Need.',
      'money.',
      'you have leftover GPUs from mining Bitcoin.'];
    let timer = setTimeout(() => typeWriter(text), 50);
    return () => {
      clearTimeout(timer);
    }
  }, [stringIndex, charIndex]);

  let speed = 50;
  let pause = 1000;

  function typeWriter(text) {
    if (document.getElementById("typedtext") != null){
      if (charIndex < text[stringIndex].length) {
        document.getElementById("typedtext").innerHTML += text[stringIndex].charAt(charIndex);
        setCharIndex(prevIndex => prevIndex+1);
        setTimeout(() => { typeWriter(text) }, speed);
      }
      else {
        setTimeout(() => { typeDeleter(text) }, pause);
      }
    }
  }
  function typeDeleter(text) {
    if (document.getElementById("typedtext") != null){
      if (charIndex > 0) {
        document.getElementById("typedtext").innerHTML = document.getElementById("typedtext").innerHTML.slice(0, -1);
        setCharIndex(prevIndex => prevIndex-1)
        setTimeout(() => { typeDeleter(text) }, speed);
      }
      else {
        setStringIndex(prevIndex => (prevIndex+1) % text.length);
        setTimeout(() => { typeWriter(text) }, pause);
      }
    }
  }

  return (
  <div className="home">
    <div className="tagline">
      <h1>Deep Learning</h1>
      <h5 id="typedtext">Because </h5>
    </div>
  </div>);
}
export default Home;
