import React from 'react';
import { Link, useMatch, useResolvedPath } from 'react-router-dom';
import './NavBar.css';

function NavBar() {
  return (
    <nav>
      <ul>
        <CustomLink to="/home" className="link">Home</CustomLink>
        <CustomLink to="/about" className="link">About</CustomLink>
        <CustomLink to="/unit1" className="link">About</CustomLink>
        <CustomLink to="/unit2" className="link">About</CustomLink>
        <CustomLink to="/unit3" className="link">About</CustomLink>
      </ul>
    </nav>
  );
};
export default NavBar;

function CustomLink({ to, children, ...props }) {
  const resolvedPath = useResolvedPath(to);
  const isActive = useMatch({ path: resolvedPath.pathname, end: true });
  return (
    <li>
      <Link to={to} {...props}>
        {children}
      </Link>
      <div className={isActive ? "active" : ""} />
    </li>
  );
};
