import React, { useState } from 'react';

function PageNavBar({ unit, currIndex, pageList, topics }) {
    const [pageIndex, setPageIndex] = useState(currIndex);

    // define event handlers 
    const goBack = () => {
        setPageIndex(prevIndex => prevIndex - 1);
    };

    const goNext = () => {
        setPageIndex(prevIndex => prevIndex + 1);
    };

    // determine if on the first question or not 
    const onFirstPage = pageIndex === 1;
    const onLastPage = pageIndex === pageList.length;

    let backButton = <></>;
    let nextButton = <></>;
    if (!onFirstPage) {
        <CustomLink to={`/unit${unit}/${pageIndex-1}`} className="prevPage">Home</CustomLink>
        backButton = <button onClick={goBack}>{topics[(pageIndex-1)-1]}</button>
    }
    if(!onLastPage){
        <CustomLink to={`/unit${unit}/${pageIndex+1}`} className="nextPage">Home</CustomLink>
        nextButton = <button onClick={goNext}>{topics[(pageIndex+1)-1]}</button>
    }

    return (
        <>
            {backButton}
            {nextButton}
        </>
    );
}
export default PageNavBar;
